import React, { useEffect } from "react";
import styled from "styled-components";
import { theme } from "../../../config/colorTheme";

import Amplify, { Auth } from "aws-amplify";
import {
  AmplifyAuthenticator,
  AmplifySignUp,
  AmplifySignIn,
  AmplifySignOut,
} from "@aws-amplify/ui-react";

const Container = styled.div`
  position: absolute;
  width: 40vw;
  top: 5vw;
  height: 50vw;
  left: 30vw;
  border: solid 4px ${theme.secondaryColor};
  z-index: 300;
  background-color: ${theme.white};
  margin-bottom: 10vw;
`;

var APICalls = require("../../../utils/APICalls");
//confirmSignUp
export default function AuthForm() {
  const handleAuthChange = async (nextAuthState, data) => {
    data &&
      data.username &&
      nextAuthState == "signedin" &&
      APICalls.readUserByCognitoUsername(data.username, (userData) => {
        if (!userData || !userData._id) {
          APICalls.createNewUser(
            { cognito_username: data.username },
            (newUserData) => {
              props.onUserAvailable(newUserData);
            }
          );
        } else props.onUserAvailable(userData);
      });
  };
  return (
    <Container>
      <AmplifyAuthenticator
        usernameAlias="email"
        handleAuthStateChange={(nextAuthState, data) =>
          handleAuthChange(nextAuthState, data)
        }
      >
        <AmplifySignUp
          slot="sign-up"
          usernameAlias="email"
          formFields={[
            {
              type: "email",
              label: "Email Address",
              required: true,
            },
            {
              type: "password",
              label: "Password",
              required: true,
            },
          ]}
        />
        <AmplifySignIn slot="sign-in" usernameAlias="email" />
        <AmplifySignOut></AmplifySignOut>
      </AmplifyAuthenticator>
    </Container>
  );
}
