import React, { Component } from "react";
import styled from "styled-components";
import { theme } from "../../config/colorTheme"
import { devices } from "../../config/devices.js"
import {animationKeyFrames} from "../../config/keyframes"


const Container = styled.div`
  position:relative;
  border: solid 1px ${theme.secondaryColor};
  width:18vw;
  overflow:scroll;
  padding:1vw;
  margin:0.5vw;
  background-color:#ffffff;
  height:25vh;

  @media ${devices.mobile} {
    width:50vw;
    height:50vw;
    max-height:50vw;
  }
`;

const Title = styled.textarea`
  font-size: 1.2vw
  margin:0;
  font-family: "Roboto";
  font-weight: 700;
  resize: none;
  border: none;
  overflow: auto;
  outline: none;
  text-align:left;
  max-width:95%;


  @media ${devices.mobile} {
    margin: 2vw auto;
    margin-bottom:0;
    font-size: 4vw;
  }
`;

const Description = styled.textarea`
  font-size: 1.1vw;
  font-family: 'Roboto Slab', serif;
  font-weight: 300;
  margin:0;
  padding:0;
  text-align:left;
  resize: none;
  border: none;
  overflow: auto;
  outline: none;
  max-width:95%;
  min-height:80%

  @media ${devices.mobile} {
    font-size: 3vw;
    text-align:center;
    margin: 1vw auto;
    width:95%;
    min-height:40%;

  }
`;

const AddButton = styled.button`
border-radius:.8vw;
border: solid 1px ${props => props.disabled ? 'lightgrey' : theme.secondaryColor};
background-color:#ffffff;
transition: transform .3s;
cursor: ${props => props.disabled ? 'not-allowed' : 'pointer'};
text-align: center;
vertical-align: middle;
padding:0.5vw 1vw;
&:hover{
  transform: scale(1.04);
   background-color:${props => props.disabled ? '#ffffff' : theme.secondaryColor};
  color:${props => props.disabled ? 'lightgrey' : '#ffffff'};
}
font-size: 1.2vw
margin:auto;
margin-bottom: 0vw;
font-family: "Nunito";
font-weight: 900
color:${props => props.disabled ? 'lightgrey' : theme.secondaryColor};
outline:none;

@media ${devices.mobile} {
  font-size: 3vw;
  margin: 1vw auto;
  padding:2vw;

}
`;

const FeatureButton = styled.img`
position:absolute;
bottom:0.5vw;
right:0.5vw;
width:2vw;
height:2vw;
animation: ${animationKeyFrames.fadeIn} 0.5s ease;


@media ${devices.mobile} {
  font-size: 3vw
  width:auto;
  height:auto;
  max-height:none;
  padding:2vw 2vw;
}
`;

const TITLE_PLACEHOLDER = "Dream feature name";
const DESCRIPTION_PLACEHOLDER = "Breifly describe what the dream feature is.";

class NewFeature extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      titlePlaceholder: TITLE_PLACEHOLDER,
      description: "",
      descriptionPlaceholder: DESCRIPTION_PLACEHOLDER,
      topic:props.topicID
    };
  }

  saveNewFeature() {

  }

  render() {
    return (
      <Container>
        <Title
          placeholder={this.state.titlePlaceholder}
          onClick={(ev) => { this.setState({ titlePlaceholder: "" }) }}
          onBlur={(ev) => { this.setState({ titlePlaceholder: TITLE_PLACEHOLDER }) }}
          value={this.state.name}
          onChange={(ev) => this.onTitleChange(ev)}
          onKeyPress={(ev) => this.onTitleKeyPress(ev)}>
        </Title>

        <Description
          ref="description"
          placeholder={this.state.descriptionPlaceholder}
          onClick={(ev) => { this.setState({ descriptionPlaceholder: "" }) }}
          onBlur={(ev) => { this.setState({ descriptionPlaceholder: DESCRIPTION_PLACEHOLDER }) }}
          value={this.state.description}
          onChange={(ev) => this.onDescriptionChange(ev)}>
        </Description>

        {this.state.name.length > 0 && this.state.description.length > 0 && 
        <FeatureButton src={require('./assets/add_icon.svg')} onClick={() => this.handleAddClick()}></FeatureButton>}

      </Container>
    )
    return null;
  }

  onTitleChange(ev) {
    this.setState({ name: ev.target.value });
  }

  onTitleKeyPress(ev) {
    if (ev.key === 'Enter') {
      ev.preventDefault();
      this.refs.description.focus();
    }
  }

  onDescriptionChange(ev) {
    this.setState({ description: ev.target.value });
  }

  renderAddButton() {
    if (this.state.name.length > 0 && this.state.description.length > 0) {
      return <AddButton disabled={false} onClick={() => this.handleAddClick()}>Add to drone</AddButton>
    }
    return <AddButton disabled={true}>Add to drone</AddButton>
  }

  handleAddClick() {
    fetch('/features/',
      {
        method: 'POST',
        body: JSON.stringify(this.state),
        headers: {
          'Accept': 'application/json, text/plain, */*',
          'Content-Type': 'application/json',
          'Mode': "CORS"
        }
      }).then(response => response.json())
      .then(data => {
        this.props.clickHandler(data);
        this.clear();
      }
      );
  }

  clear() {
    this.setState({ name: "", description: "" })
  }
}

export default NewFeature;
